import React, { Component } from 'react'
import { Route, Link, Redirect } from 'react-router-dom'
import { List, Grid, Menu, Segment, Placeholder, Header, Container } from 'semantic-ui-react'


// test components for routes
const Public = () => (
  <div>
    <h2>Public</h2>
  </div>
);

const Private = () => (
  <div>
    <h2>Private</h2>
  </div>
);

const Login = () => (
  <div>
    <h2>Login Page <button>login</button></h2>
  </div>
);

const AuthService = {
  isAuthenticated: false,
  authenticate(cb) {
    this.isAuthenticated = true
    setTimeout(cb, 100)
  },
  logout(cb) {
    this.isAuthenticated = false
    setTimeout(cb, 100)
  }
}

const SecretRoute = ({ component: Component, ...rest }) => (
  <Route {...rest} render={(props) => (
    AuthService.isAuthenticated === true
    ? <Component {...props} />
    : <Redirect to='/login' />
  )} />
)

const Rules = ({ match }) => (
  <div>
    <h2>Rules</h2>
      <List>
        <List.Item as={ Link } to={`${match.url}/rule1`}>Rule 1</List.Item>
        <List.Item as={ Link } to={`${match.url}/rule2`}>Rule 2</List.Item>
        <List.Item as={ Link } to={`${match.url}/rule3`}>Rule 3</List.Item>
      </List>
      <Route exact path={`${match.path}/:rule`} render={({match}) => (<div>This is {match.params.course}</div>)}/>         
  </div>
);



export default class App extends Component {
  state = { activeItem: 'Public' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <Grid>
        <Grid.Row columns={2}>
          <Grid.Column width={3}>
          <Placeholder style={{ height: 150, width: 220 }}>
            <Placeholder.Image />
          </Placeholder>
          </Grid.Column>
          <Grid.Column width={13}>
            <Header as='h1' textAlign='center'>Guardian</Header>
          </Grid.Column>
        </Grid.Row>
        <Grid.Row columns={2}>
          <Grid.Column width={3}>
            <Menu fluid vertical tabular>
              <Menu.Item as={ Link } name='Public' to='/' active={activeItem === 'Public'} onClick={this.handleItemClick} />
              <Menu.Item as={ Link } name='Private' to='/Private' active={activeItem === 'Private'} onClick={this.handleItemClick} />               
            </Menu>
          </Grid.Column>
          <Grid.Column stretched width={13}>
            <Segment>
              <Header as='h1'>Application Header</Header>
              <Container>
                <Route path='/Public' component={Public}/>
                <SecretRoute path='/Private' component={Private}/>                                
              </Container>
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    )
  }
}
